import React, { useState, useEffect } from 'react';

// This context will change the theme of the app based on the time of day for the user
// For now, just changes the background gradient

const themes = {
    dawn: {
        backgroundGradient: "blue"
    },
    day: {
        backgroundGradient: "red"
    },
    dusk: {
        backgroundGradient: "orange"
    },
    night: {
        backgroundGradient: "black"
    }
};

const initialState = {
    time: '',
    theme: themes.day,
    toggle: () => {}
};

export const ThemeContext = React.createContext(initialState);

const ThemeProvider = ({ children }) => {
    const [time, setTime] = useState('day');

    useEffect(time => {
        // Set theme based on time of day
        // Dawn 6-9, day 9-17, Dusk 17-20, Night 20 - 6
        const currentHour = new Date().getHours();
        // if (currentHour < 6 || currentHour > 20) {
        //     setTime('night');
        // } else if (currentHour >= 6 && currentHour <= 9) {
        //     setTime('dawn');
         if (currentHour > 9 && currentHour < 17) {
            setTime('day');
        } else if (currentHour >= 17 && currentHour <= 20) {
            setTime('dusk')
        }
        console.log(currentHour)
        console.log(theme);
    }, [time])

    const theme = themes[time];

    return (
        <ThemeContext.Provider value={{ time }}>
            { children }
        </ThemeContext.Provider>
    )
};

export default ThemeProvider;
