import React, { useState, useContext } from 'react';
import { TodoContext } from '../context/TodoProvider';

const AddTodo = props => {
    const [input, setInput] = useState('New Task');
    const { handleSetNewTodo } = useContext(TodoContext);

    const handleInputChange = (e) => {
        e.preventDefault();
        setInput(e.target.value);
    }

    return (
        <form>
            <input value={input} onChange={(e) => handleInputChange(e) } />
            <button type="button" onClick={() => handleSetNewTodo(input) }>Add</button>
        </form>
    )
}

export default AddTodo;Â
