import React, { useContext } from 'react';

// State
import { TodoContext } from "../context/TodoProvider";

// Comnponents
import AddTodo from "./AddTodo";
import CompleteButton from "./CompleteButton";
import DeleteButton from "./DeleteButton";

const List = () => {
    const { list, handleCompleteTask, handleDeleteTask } = useContext(TodoContext);
    return (
        <div className="list">
                <AddTodo />                
                {(list.todos.length > 0) ? (list.todos.map((item, i) => {
                    return (
                        <ul>
                            <li key={i}>
                                <CompleteButton action={() => handleCompleteTask(item.id)} />
                                <span className={`task-text ${item.completed ? 'completed-task' : ''}`}>{item.task}</span>
                                <span>{item.date}</span>
                                <DeleteButton action={() => handleDeleteTask(item.id)} />
                            </li>
                        </ul>
                    );
                })) : (
                    <div className="empty-list">Nothing to do!</div>
                )}
        </div>
    );
}

export default List;
