import React, { useState } from 'react';

export const TodoContext = React.createContext();

// const todoformat = {
//     text: '',
//     date: '',
//     completed: false
// }

// const todoExample = {
//     text: 'first todo',
//     date: '11/12/13',
//     completed: false
// }

const TodoProvider = props => {
    const [todo, setTodo] = useState('');
    const [list, setList] = useState({ todos: [] });

    const handleSetNewTodo = task => {
        const taskId = list.length + 1;
        const newTodo = { 
            id: taskId,
            task: task,
            date: "",
            completed: false
        };

        setTodo(newTodo);
        handleAddToList(newTodo)
    }

    const handleAddToList = item => {
        setList((prevState) => ({
            todos: [...prevState.todos, item]
        }))
    }

    const handleCompleteTask = task => {
        if (task.completed) {
            // const 
            // setList(prevState => ({
            //     todos
            // }))
        }
    }

    return (
        <TodoContext.Provider 
            value={{
                handleSetNewTodo,
                handleAddToList,
                todo,
                list
            }}
        >
            { props.children }
        </TodoContext.Provider>
    )

}

export default TodoProvider;
