//     date: '11/12/13',
//     completed: false
// }

const TodoProvider = props => {
    const [todo, setTodo] = useState('');
    const [list, setList] = useState({ todos: [] });

    const handleSetNewTodo = item => {
        setTodo(item);
        console.log(item);
        handleAddToList(item)
    }

    const handleAddToList = (item, date) => {
        setList((prevState, i) => ({
            todos: [...prevState.todos, { id: i, task: item, date: "", completed: false }]
        }))
    }

    const handleCompleteTask = task => {
        if (task.completed) {
            // const 
            // setList(prevState => ({
            //     todos
            // }))
        }
    }

    return (
        <TodoContext.Provider 
            value={{
                handleSetNewTodo,
                handleAddToList,
                todo,
                list
            }}
        >
            { props.children }
        </TodoContext.Provider>
    )

}

export default TodoProvider;
