import React, { useState } from 'react';

export const TodoContext = React.createContext();

// const todoformat = {
//     text: '',
//     date: '',
//     completed: false
// }

// const todoExample = {
//     text: 'first todo',
//     date: '11/12/13',
//     completed: false
// }

const TodoProvider = props => {
    const [todo, setTodo] = useState('');
    const [list, setList] = useState({ todos: [] });

    const handleSetNewTodo = (task, date) => {
        // Todo: add date functionality
        const taskId = list.todos.length + 1;
        const newTodo = { 
            id: taskId,
            task: task,
            date: "",
            completed: false
        };

        setTodo(newTodo);
        handleAddToList(newTodo)
    }

    const handleAddToList = item => {
        setList((prevState) => ({
            todos: [...prevState.todos, item]
        }))
    }

    const handleCompleteTask = (id) => {
        const tempState = list.todos;
        for (let i = 0; i < tempState.length; i++) {
            if (tempState[i].id === id) {
                tempState[i].completed = true;
                console.log(tempState[i])
            }
        }
        setList(tempState);
    }

    const handleDeleteTask = id => {
        const newState = list.todos.filter(task => id !== task.id);
        setList((prevState) => ({
            todos: [...prevState, newState]
        }));
    }

    return (
        <TodoContext.Provider 
            value={{
                handleSetNewTodo,
                handleAddToList,
                handleCompleteTask,
                handleDeleteTask,
                todo,
                list
            }}
        >
            { props.children }
        </TodoContext.Provider>
    )

}

export default TodoProvider;
