import React, { useState, useContext } from 'react';
import { TodoContext } from '../context/TodoProvider';

const AddTodo = props => {
    const [input, setInput] = useState('Add a new task');
    const { handleSetNewTodo } = useContext(TodoContext);

    const handleInputChange = e => {
        setInput(e.target.value);
    }

    const handleSubmit = e => {
        e.preventDefault();
        if (input) {
            handleSetNewTodo(input.toString());
            setInput('');
        }
    }

    const handleClearInput = () => {
        setInput('');
    }

    return (
        <form className="add-task" onSubmit={ handleSubmit }>
            <input value={input} onClick={handleClearInput} onChange={ handleInputChange } />
            <button type="button" className="add-task-button" onClick={ handleSubmit }>+</button>
        </form>
    )
}

